INSERT INTO smartquitiot.mission (id, code, condition_json, created_at, description, exp, name, phase, status, updated_at, interest_category_id, mission_type_id) VALUES (1, 'PREP_WRT_FUTURE_LETTER', null, '2025-12-12 11:45:27.000000', 'Write a short letter to your future self about why you want to quit and how proud you will feel staying smoke free.', 100, 'Write a Letter to Your Future Self', 'PREPARATION', 'ACTIVE', '2025-12-12 11:45:27.000000', null, 3);
INSERT INTO smartquitiot.mission (id, code, condition_json, created_at, description, exp, name, phase, status, updated_at, interest_category_id, mission_type_id) VALUES (2, 'PREP_CLEAR_SMOKING_ITEMS', null, '2025-12-12 11:45:27.000000', 'Remove all cigarettes, lighters, ashtrays, and related items from your home, workspace, and vehicle to break environmental cues.', 100, 'Clear Out Smoking Items', 'PREPARATION', 'ACTIVE', '2025-12-12 11:45:27.000000', null, 4);
INSERT INTO smartquitiot.mission (id, code, condition_json, created_at, description, exp, name, phase, status, updated_at, interest_category_id, mission_type_id) VALUES (3, 'PREP_POST_TOP_REASON', null, '2025-12-12 11:45:27.000000', 'Choose your most important reason (health, family, savings, self-image) and place it somewhere highly visible as a daily motivator.', 100, 'Post Your Top1 Reason Where You Can See It', 'PREPARATION', 'ACTIVE', '2025-12-12 11:45:27.000000', null, 5);
INSERT INTO smartquitiot.mission (id, code, condition_json, created_at, description, exp, name, phase, status, updated_at, interest_category_id, mission_type_id) VALUES (4, 'PREP_LEARN_HARMS', null, '2025-12-12 11:45:27.000000', 'Read a credible article or watch a short video about how smoking affects you, your family, and society. Summarize 3 takeaways.', 100, 'Learn the Harms of Smoking', 'PREPARATION', 'ACTIVE', '2025-12-12 11:45:27.000000', null, 5);
INSERT INTO smartquitiot.mission (id, code, condition_json, created_at, description, exp, name, phase, status, updated_at, interest_category_id, mission_type_id) VALUES (5, 'PREP_LEARN_NRT', '{"logic": "AND", "rules": [{"field": "use_nrt", "value": true, "operator": "="}]}', '2025-12-12 11:45:27.000000', 'Explore patches, gum, lozenges, inhalers, and sprays. Consider which option fits your lifestyle and prepare questions for a clinician.', 100, 'Learn About Nicotine Replacement Therapy (NRT)', 'PREPARATION', 'ACTIVE', '2025-12-12 11:45:27.000000', null, 6);
INSERT INTO smartquitiot.mission (id, code, condition_json, created_at, description, exp, name, phase, status, updated_at, interest_category_id, mission_type_id) VALUES (6, 'PREP_SKIP_BREAKFAST_SMOKE', '{"logic": "AND", "rules": [{"field": "morning_smoking_frequency", "value": true, "operator": "="}]}', '2025-12-12 11:45:27.000000', 'Experiment with not smoking during breakfast. Replace it with water, fruit, or a brief walk to test a smoke free morning routine. Outcome does not need to be perfect.', 100, 'Try Skipping the Breakfast Cigarette', 'PREPARATION', 'ACTIVE', '2025-12-12 11:45:27.000000', null, 2);
INSERT INTO smartquitiot.mission (id, code, condition_json, created_at, description, exp, name, phase, status, updated_at, interest_category_id, mission_type_id) VALUES (7, 'PREP_LIST_TRIGGERS', null, '2025-12-12 11:45:27.000000', 'List trigger events (Morning, After Meal, Gaming, Party, Coffee, Stress, Boredom, Driving, Sadness, Work). For each, define one alternative action you can take instead.', 100, 'List Your Smoking Triggers', 'PREPARATION', 'ACTIVE', '2025-12-12 11:45:27.000000', null, 5);
INSERT INTO smartquitiot.mission (id, code, condition_json, created_at, description, exp, name, phase, status, updated_at, interest_category_id, mission_type_id) VALUES (8, 'PREP_TEST_SMALL_REDUCTION', '{"logic": "AND", "rules": [{"field": "smoke_avg_per_day", "value": 10, "operator": ">="}]}', '2025-12-12 11:45:27.000000', 'Try reducing the number of cigarettes you smoke today by a small amount just to see what helps. The outcome does not need to be perfect.', 100, 'Test a Small Reduction in Cigarettes', 'PREPARATION', 'ACTIVE', '2025-12-12 11:45:27.000000', null, 1);
INSERT INTO smartquitiot.mission (id, code, condition_json, created_at, description, exp, name, phase, status, updated_at, interest_category_id, mission_type_id) VALUES (9, 'PREP_DELAY_FIRST_CIG', '{"logic": "AND", "rules": [{"field": "minutes_after_waking_to_smoke", "value": 60, "operator": "<="}]}', '2025-12-12 11:45:27.000000', 'After waking up, wait 15–20 minutes before your first cigarette. Practice breathing or drink water to observe your habit loop.', 100, 'Delay Your First Cigarette by 15–20 Minutes', 'PREPARATION', 'ACTIVE', '2025-12-12 11:45:27.000000', null, 2);
INSERT INTO smartquitiot.mission (id, code, condition_json, created_at, description, exp, name, phase, status, updated_at, interest_category_id, mission_type_id) VALUES (10, 'PREP_TELL_SUPPORT_PERSON', null, '2025-12-12 11:45:27.000000', 'Tell someone you trust about your quit plan and ask them to check in on you.', 100, 'Tell a Close Friend or Family Member', 'PREPARATION', 'ACTIVE', '2025-12-12 11:45:27.000000', null, 3);
INSERT INTO smartquitiot.mission (id, code, condition_json, created_at, description, exp, name, phase, status, updated_at, interest_category_id, mission_type_id) VALUES (11, 'PREP_LEARN_NEW_SKILL', null, '2025-12-12 11:45:27.000000', 'Learn a simple new skill to keep your hands and mind busy and help unpair daily routines from cigarettes.', 100, 'Learn a New Skill to Replace Smoking', 'PREPARATION', 'ACTIVE', '2025-12-12 11:45:27.000000', null, 2);
INSERT INTO smartquitiot.mission (id, code, condition_json, created_at, description, exp, name, phase, status, updated_at, interest_category_id, mission_type_id) VALUES (12, 'PREP_BUDGET_SAVINGS_PLAN', null, '2025-12-12 11:45:27.000000', 'Make a plan for how you will use your saved money from not buying cigarettes to purchase something you truly enjoy.', 110, 'Plan Your Savings Reward', 'PREPARATION', 'ACTIVE', '2025-12-12 11:45:27.000000', null, 3);
INSERT INTO smartquitiot.mission (id, code, condition_json, created_at, description, exp, name, phase, status, updated_at, interest_category_id, mission_type_id) VALUES (13, 'PREP_SPORTS_MORNING_STRETCH', '{"logic": "AND", "rules": [{"field": "morning_smoking_frequency", "value": true, "operator": "="}]}', '2025-12-12 11:45:27.000000', 'Start your morning with a 7minute warm up (joint rotations, light stretches) to break the first cigarette cue and boost energy.', 110, 'Morning Mobility 7 mins', 'PREPARATION', 'ACTIVE', '2025-12-12 11:45:27.000000', 2, 2);
INSERT INTO smartquitiot.mission (id, code, condition_json, created_at, description, exp, name, phase, status, updated_at, interest_category_id, mission_type_id) VALUES (14, 'PREP_SPORTS_STEP_GOAL', null, '2025-12-12 11:45:27.000000', 'Set a goal of 6000 to 8000 steps per day during the preparation week. Choose walking time instead of smoking breaks.', 120, 'Set a Daily Step Goal', 'PREPARATION', 'ACTIVE', '2025-12-12 11:45:27.000000', 2, 2);
INSERT INTO smartquitiot.mission (id, code, condition_json, created_at, description, exp, name, phase, status, updated_at, interest_category_id, mission_type_id) VALUES (15, 'PREP_SPORTS_MICRO_STRETCH_BREAKS', null, '2025-12-12 11:45:27.000000', 'Schedule three 2minute stretch breaks in your day at times you usually smoke.', 110, 'Micro-Stretch Breaks', 'PREPARATION', 'ACTIVE', '2025-12-12 11:45:27.000000', 2, 2);
INSERT INTO smartquitiot.mission (id, code, condition_json, created_at, description, exp, name, phase, status, updated_at, interest_category_id, mission_type_id) VALUES (16, 'PREP_SPORTS_BEGINNER_YOGA', null, '2025-12-12 11:45:27.000000', 'Follow a short beginner yoga routine to replace one smoking break today.', 110, 'Beginner Yoga Flow (8 to 10 min)', 'PREPARATION', 'ACTIVE', '2025-12-12 11:45:27.000000', 2, 2);
INSERT INTO smartquitiot.mission (id, code, condition_json, created_at, description, exp, name, phase, status, updated_at, interest_category_id, mission_type_id) VALUES (17, 'PREP_ART_SMOKEFREE_POSTER', null, '2025-12-12 11:45:27.000000', 'Create your own poster or wallpaper as a personal reminder of your quit reason. Print it or set it as your phone background.', 110, 'Design a SmokeFreeMe Poster', 'PREPARATION', 'ACTIVE', '2025-12-12 11:45:27.000000', 3, 2);
INSERT INTO smartquitiot.mission (id, code, condition_json, created_at, description, exp, name, phase, status, updated_at, interest_category_id, mission_type_id) VALUES (18, 'PREP_ART_MANDALA_COLORING', null, '2025-12-12 11:45:27.000000', 'Color a mandala for 10 minutes whenever you crave a cigarette to keep your hands busy and calm your mind.', 100, 'Mandala Coloring 10Minute', 'PREPARATION', 'ACTIVE', '2025-12-12 11:45:27.000000', 3, 2);
INSERT INTO smartquitiot.mission (id, code, condition_json, created_at, description, exp, name, phase, status, updated_at, interest_category_id, mission_type_id) VALUES (19, 'PREP_ART_HAND_CRAFT_KIT', null, '2025-12-12 11:45:27.000000', 'Prepare a small kit (pen, mini sketchbook, washi tape) to grab instead of a cigarette.', 110, 'Assemble a HandCraft Kit', 'PREPARATION', 'ACTIVE', '2025-12-12 11:45:27.000000', 3, 2);
INSERT INTO smartquitiot.mission (id, code, condition_json, created_at, description, exp, name, phase, status, updated_at, interest_category_id, mission_type_id) VALUES (20, 'PREP_ART_PHOTO_CHALLENGE', null, '2025-12-12 11:45:27.000000', 'Take three themed photos (color, texture, symmetry) during times you would normally smoke.', 100, '3 Photo Mood Challenge', 'PREPARATION', 'ACTIVE', '2025-12-12 11:45:27.000000', 3, 2);
INSERT INTO smartquitiot.mission (id, code, condition_json, created_at, description, exp, name, phase, status, updated_at, interest_category_id, mission_type_id) VALUES (21, 'PREP_COOK_SMART_SNACK_BOX', null, '2025-12-12 11:45:27.000000', 'Prepare a healthy snack box (nuts, dried fruits, sugarfree gum) to replace the hand to mouth habit of smoking.', 120, 'Prepare a CravingSnackBox', 'PREPARATION', 'ACTIVE', '2025-12-12 11:45:27.000000', 4, 4);
INSERT INTO smartquitiot.mission (id, code, condition_json, created_at, description, exp, name, phase, status, updated_at, interest_category_id, mission_type_id) VALUES (22, 'PREP_COOK_HYDRATION_PLAN', null, '2025-12-12 11:45:27.000000', 'Prepare two bottles of water (optionally with lemon or mint) to drink whenever you feel an urge to smoke.', 100, 'Hydration Prep', 'PREPARATION', 'ACTIVE', '2025-12-12 11:45:27.000000', 4, 4);
INSERT INTO smartquitiot.mission (id, code, condition_json, created_at, description, exp, name, phase, status, updated_at, interest_category_id, mission_type_id) VALUES (23, 'PREP_COOK_MEAL_PREP', null, '2025-12-12 11:45:27.000000', 'Plan and prep one balanced meal to stabilize hunger related triggers.', 110, 'Simple Meal Prep', 'PREPARATION', 'ACTIVE', '2025-12-12 11:45:27.000000', 4, 4);
