INSERT INTO smartquitiot.news (id, content, created_at, status, thumbnail_url, title) VALUES (1, 'Smoking is more than just a habit – it is a slow and dangerous attack on your entire body.
INSERT INTO smartquitiot.news (id, content, created_at, status, thumbnail_url, title) VALUES (2, 'Many people believe nicotine itself is the main cause of smoking-related diseases.
INSERT INTO smartquitiot.news (id, content, created_at, status, thumbnail_url, title) VALUES (3, 'Nicotine cravings often feel intense, but they are temporary by nature.
INSERT INTO smartquitiot.news (id, content, created_at, status, thumbnail_url, title) VALUES (4, 'Quitting smoking is not a straight line.
INSERT INTO smartquitiot.news (id, content, created_at, status, thumbnail_url, title) VALUES (5, 'Clean nicotine refers to nicotine delivered without burning tobacco, such as patches, gum, or lozenges.
INSERT INTO smartquitiot.news (id, content, created_at, status, thumbnail_url, title) VALUES (6, 'Quitting smoking can feel lonely, but you are not alone in this journey.
