INSERT INTO smartquitiot.phase_detail_mission (id, code, completed_at, description, name, status, mission_id, phase_detail_id) VALUES (938, 'PEAK_NRT_GUM_TECHNIQUE_HEART', '2025-12-25 09:59:27.075627', 'Cravings are strongest now - chew NRT gum until you feel the peppery taste, then park it between your gum and cheek. Repeat as needed.', 'Master the Park and Chew Method', 'COMPLETED', 173, 245);
INSERT INTO smartquitiot.phase_detail_mission (id, code, completed_at, description, name, status, mission_id, phase_detail_id) VALUES (939, 'PEAK_SPORTS_SHAKE_RELEASE', null, 'Shake your arms and legs for 30 seconds to reset your body is stress response. Feel lighter instantly.', 'Tension Shake Off', 'INCOMPLETED', 105, 245);
INSERT INTO smartquitiot.phase_detail_mission (id, code, completed_at, description, name, status, mission_id, phase_detail_id) VALUES (940, 'PEAK_COOK_COLD_DRINK', null, 'Drink cold water slowly in sips. Cooling your mouth mimics the inhale–exhale rhythm, but healthier.', 'Cold Water Boost', 'INCOMPLETED', 110, 245);
INSERT INTO smartquitiot.phase_detail_mission (id, code, completed_at, description, name, status, mission_id, phase_detail_id) VALUES (941, 'PEAK_URGE_SURFING', null, 'Focus on your breath and body for 3 minutes. Observe the craving wave until it fades naturally.', 'Surf Through the Intensity', 'INCOMPLETED', 140, 246);
INSERT INTO smartquitiot.phase_detail_mission (id, code, completed_at, description, name, status, mission_id, phase_detail_id) VALUES (942, 'PEAK_NRT_TRACK_PROGRESS', null, 'Log each craving you overcame with NRT. Seeing progress builds motivation and trust in the method.', 'Track Your NRT Success', 'INCOMPLETED', 149, 246);
INSERT INTO smartquitiot.phase_detail_mission (id, code, completed_at, description, name, status, mission_id, phase_detail_id) VALUES (943, 'PEAK_SPORTS_INTENSE_WALK', null, 'When cravings spike, walk briskly or jog in place for 4 minutes. Let the energy release instead of smoke.', 'Power Walk Burst', 'INCOMPLETED', 121, 246);
INSERT INTO smartquitiot.phase_detail_mission (id, code, completed_at, description, name, status, mission_id, phase_detail_id) VALUES (944, 'PEAK_TOOTHBRUSH_RESET', null, 'Brush your teeth or rinse with mint mouthwash to erase smoking taste cues and create freshness.', 'Mint Refresh', 'INCOMPLETED', 144, 246);
INSERT INTO smartquitiot.phase_detail_mission (id, code, completed_at, description, name, status, mission_id, phase_detail_id) VALUES (945, 'PEAK_NRT_SAFETY_TIP', null, 'Do not smoke while using NRT products. Mixing both increases nicotine levels and may cause discomfort.', 'Follow Safe NRT Usage', 'INCOMPLETED', 151, 247);
INSERT INTO smartquitiot.phase_detail_mission (id, code, completed_at, description, name, status, mission_id, phase_detail_id) VALUES (946, 'PEAK_SPORTS_BOX_BREATH', null, 'When urge spikes, do box breathing: inhale 4s, hold 4s, exhale 4s, hold 4s like steady rhythm before a race.', 'Box Breathing Drill', 'INCOMPLETED', 103, 247);
INSERT INTO smartquitiot.phase_detail_mission (id, code, completed_at, description, name, status, mission_id, phase_detail_id) VALUES (947, 'PEAK_COOK_HAND_ACTIVITY', null, 'Peel an orange, cut an apple, or prepare a light snack using your hands it keeps them busy.', 'Snack Mindfully', 'INCOMPLETED', 111, 247);
INSERT INTO smartquitiot.phase_detail_mission (id, code, completed_at, description, name, status, mission_id, phase_detail_id) VALUES (948, 'PEAK_WRITE_MOTIVATION', null, 'Write one strong reason to stay smoke-free and carry it. Read it whenever the urge hits.', 'Motivation Flashcard', 'INCOMPLETED', 114, 247);
INSERT INTO smartquitiot.phase_detail_mission (id, code, completed_at, description, name, status, mission_id, phase_detail_id) VALUES (949, 'PEAK_NRT_WATER_SUPPORT', null, 'Drink a full glass of water right after using your NRT product. It helps absorption and reduces dry mouth during craving peaks.', 'Pair NRT with Water', 'INCOMPLETED', 175, 248);
INSERT INTO smartquitiot.phase_detail_mission (id, code, completed_at, description, name, status, mission_id, phase_detail_id) VALUES (950, 'PEAK_SPORTS_SQUAT_BURST', null, 'Drop into 20 squats and exhale with each movement. Feel the strength returning to your body.', '20 Squats Challenge', 'INCOMPLETED', 122, 248);
INSERT INTO smartquitiot.phase_detail_mission (id, code, completed_at, description, name, status, mission_id, phase_detail_id) VALUES (951, 'PEAK_ART_CRAVING_SHAPE', null, 'Use your pen to draw what your craving looks like. Externalize it, then cross it out boldly.', 'Shape the Urge', 'INCOMPLETED', 106, 248);
INSERT INTO smartquitiot.phase_detail_mission (id, code, completed_at, description, name, status, mission_id, phase_detail_id) VALUES (952, 'PEAK_SOS_TEXT', null, 'Text or call your support contact and tell them: Strong craving now, please check in with me soon.', 'Reach Out Immediately', 'INCOMPLETED', 142, 248);
INSERT INTO smartquitiot.phase_detail_mission (id, code, completed_at, description, name, status, mission_id, phase_detail_id) VALUES (953, 'PEAK_NRT_REWARD_DAY', null, 'You stayed consistent with your NRT today - treat yourself with something small and positive.', 'Reward Yourself for Using NRT', 'INCOMPLETED', 150, 249);
INSERT INTO smartquitiot.phase_detail_mission (id, code, completed_at, description, name, status, mission_id, phase_detail_id) VALUES (954, 'PEAK_PAUSE_BEFORE_NICOTINE', null, 'When a strong craving appears, pause and take a few deep breaths before deciding whether to use nicotine.', 'Pause Before Using Nicotine', 'INCOMPLETED', 297, 249);
INSERT INTO smartquitiot.phase_detail_mission (id, code, completed_at, description, name, status, mission_id, phase_detail_id) VALUES (955, 'PEAK_MUSIC_POWER_SONG', null, 'Play a high-energy song and sing or move to it. Replace tension with rhythm.', 'One Powerful Song', 'INCOMPLETED', 133, 249);
INSERT INTO smartquitiot.phase_detail_mission (id, code, completed_at, description, name, status, mission_id, phase_detail_id) VALUES (956, 'PEAK_REFLECT_LEARN', null, 'Note one thing you learned about yourself today during this craving. Awareness builds mastery.', 'Reflect and Learn', 'INCOMPLETED', 132, 249);
INSERT INTO smartquitiot.phase_detail_mission (id, code, completed_at, description, name, status, mission_id, phase_detail_id) VALUES (957, 'PEAK_NRT_REPLACEMENT_READY', null, 'Get your next NRT patch, gum, or lozenge ready before the craving window begins - preparation prevents relapse.', 'Prepare Your Next NRT Dose', 'INCOMPLETED', 152, 250);
INSERT INTO smartquitiot.phase_detail_mission (id, code, completed_at, description, name, status, mission_id, phase_detail_id) VALUES (958, 'PEAK_WATER_WALK', null, 'Splash cold water on your face, drink deeply, and take a fast 5-minute walk to discharge tension.', 'Cold Water Reset and Walk', 'INCOMPLETED', 141, 250);
INSERT INTO smartquitiot.phase_detail_mission (id, code, completed_at, description, name, status, mission_id, phase_detail_id) VALUES (959, 'PEAK_AVOID_CUE_SWITCH', null, 'If coffee or a routine triggers cravings, switch to tea or stretch breaks today.', 'Break the Cue Loop', 'INCOMPLETED', 147, 250);
INSERT INTO smartquitiot.phase_detail_mission (id, code, completed_at, description, name, status, mission_id, phase_detail_id) VALUES (960, 'PEAK_CRAVING_LOG', null, 'Record what triggered your urge, rate it 1–10, and note what helped. Tracking helps regain awareness.', 'Peak Craving Log', 'INCOMPLETED', 146, 250);
INSERT INTO smartquitiot.phase_detail_mission (id, code, completed_at, description, name, status, mission_id, phase_detail_id) VALUES (961, 'PREP_LIST_TRIGGERS', null, 'List trigger events (Morning, After Meal, Gaming, Party, Coffee, Stress, Boredom, Driving, Sadness, Work). For each, define one alternative action you can take instead.', 'List Your Smoking Triggers', 'INCOMPLETED', 7, 251);
INSERT INTO smartquitiot.phase_detail_mission (id, code, completed_at, description, name, status, mission_id, phase_detail_id) VALUES (962, 'PREP_LEARN_NRT', null, 'Explore patches, gum, lozenges, inhalers, and sprays. Consider which option fits your lifestyle and prepare questions for a clinician.', 'Learn About Nicotine Replacement Therapy (NRT)', 'INCOMPLETED', 5, 251);
INSERT INTO smartquitiot.phase_detail_mission (id, code, completed_at, description, name, status, mission_id, phase_detail_id) VALUES (963, 'PREP_WRT_FUTURE_LETTER', null, 'Write a short letter to your future self about why you want to quit and how proud you will feel staying smoke free.', 'Write a Letter to Your Future Self', 'INCOMPLETED', 1, 251);
INSERT INTO smartquitiot.phase_detail_mission (id, code, completed_at, description, name, status, mission_id, phase_detail_id) VALUES (964, 'PREP_CLEAR_SMOKING_ITEMS', null, 'Remove all cigarettes, lighters, ashtrays, and related items from your home, workspace, and vehicle to break environmental cues.', 'Clear Out Smoking Items', 'INCOMPLETED', 2, 251);
INSERT INTO smartquitiot.phase_detail_mission (id, code, completed_at, description, name, status, mission_id, phase_detail_id) VALUES (965, 'PREP_POST_TOP_REASON', null, 'Choose your most important reason (health, family, savings, self-image) and place it somewhere highly visible as a daily motivator.', 'Post Your Top1 Reason Where You Can See It', 'INCOMPLETED', 3, 252);
INSERT INTO smartquitiot.phase_detail_mission (id, code, completed_at, description, name, status, mission_id, phase_detail_id) VALUES (966, 'PREP_TELL_SUPPORT_PERSON', null, 'Tell someone you trust about your quit plan and ask them to check in on you.', 'Tell a Close Friend or Family Member', 'INCOMPLETED', 10, 252);
INSERT INTO smartquitiot.phase_detail_mission (id, code, completed_at, description, name, status, mission_id, phase_detail_id) VALUES (967, 'PREP_DELAY_FIRST_CIG', null, 'After waking up, wait 15–20 minutes before your first cigarette. Practice breathing or drink water to observe your habit loop.', 'Delay Your First Cigarette by 15–20 Minutes', 'INCOMPLETED', 9, 252);
INSERT INTO smartquitiot.phase_detail_mission (id, code, completed_at, description, name, status, mission_id, phase_detail_id) VALUES (968, 'PREP_WRITE_IF_THEN_PLANS', null, 'Write five If Then scenarios (e.g., If I crave during coffee, then I will drink water and walk for 3 minutes).', 'Write If Then Plans', 'INCOMPLETED', 25, 252);
INSERT INTO smartquitiot.phase_detail_mission (id, code, completed_at, description, name, status, mission_id, phase_detail_id) VALUES (969, 'PREP_BUDGET_SAVINGS_PLAN', null, 'Make a plan for how you will use your saved money from not buying cigarettes to purchase something you truly enjoy.', 'Plan Your Savings Reward', 'INCOMPLETED', 12, 253);
INSERT INTO smartquitiot.phase_detail_mission (id, code, completed_at, description, name, status, mission_id, phase_detail_id) VALUES (970, 'PREP_COOK_HYDRATION_PLAN', null, 'Prepare two bottles of water (optionally with lemon or mint) to drink whenever you feel an urge to smoke.', 'Hydration Prep', 'INCOMPLETED', 22, 253);
INSERT INTO smartquitiot.phase_detail_mission (id, code, completed_at, description, name, status, mission_id, phase_detail_id) VALUES (971, 'PREP_READ_SUCCESS_STORIES', null, 'Read three real stories of successful quitters and write down three lessons or tips that you can apply to your own plan.', 'Read 3 Quit Success Stories', 'INCOMPLETED', 24, 253);
INSERT INTO smartquitiot.phase_detail_mission (id, code, completed_at, description, name, status, mission_id, phase_detail_id) VALUES (972, 'PREP_MUSIC_CRAVING_PLAYLIST', null, 'Build a playlist of 5 to 7 songs that distract or relax you. Play it when cravings strike during the preparation week.', 'Create a Craving Playlist', 'INCOMPLETED', 28, 253);
